-- ----------------------------
-- Table structure for users
-- ----------------------------
DROP TABLE IF EXISTS `users`;
CREATE TABLE `users`  (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `openid` varchar(128) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '微信openid',
  `parent` int(11) NULL DEFAULT 0 COMMENT '推荐人',
  `username` varchar(64) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '用户名',
  `telphone` bigint(20) NULL DEFAULT NULL COMMENT '用户手机号码',
  `avatar` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '头像',
  `sex` tinyint(1) NULL DEFAULT 0 COMMENT '性别，0保密 1男 2女',
  `sigin` int(11) NULL DEFAULT NULL COMMENT '注册日期',
  `status` tinyint(1) NULL DEFAULT 1 COMMENT '用户账号状态',
  `view_ratio` float(5, 3) NULL DEFAULT 1.000 COMMENT '用户显示的浏览量和实际显示浏览量比例',
  `percentage_ratio` float(5, 3) NULL DEFAULT 1.000 COMMENT '用户提成比例,抖音结算单次浏览量实际价格和用户收益比例',
  `recommended_ratio` float(5, 3) NULL DEFAULT 1.000 COMMENT '推荐收益比例,推荐者提收益人实际收益比例',
  `password` varchar(128) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '密码',
  PRIMARY KEY (`id`) USING BTREE,
  UNIQUE INDEX `telphone`(`telphone`) USING BTREE,
  INDEX `openid`(`openid`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 5 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_general_ci ROW_FORMAT = Dynamic;

